$(document).ready(function(){"use strict";$("#add_line_item_variant_id").change(function(){var a=$(this).val(),t=_.find(window.variants,function(t){return t.id==a});$("#stock_details").html(variantLineItemTemplate({variant:t})),$("#stock_details").show(),$("button.add_variant").click(addVariant)})}),addVariant=function(){$("#stock_details").hide();var a=$("input.variant_autocomplete").val(),t=$("input.quantity[data-variant-id='"+a+"']").val();return adjustLineItems(order_number,a,t),1},adjustLineItems=function(a,t,e){var i=Spree.routes.orders_api+"/"+a+"/line_items";$.ajax({type:"POST",url:Spree.url(i),data:{line_item:{variant_id:t,quantity:e},token:Spree.api_key}}).done(function(){window.Spree.advanceOrder(),window.location.reload()}).fail(function(a){alert(a.responseJSON.message)})};