$.fn.userAutocomplete=function(){"use strict";function e(e){return Select2.util.escapeMarkup(e.email)}this.select2({minimumInputLength:1,multiple:!0,initSelection:function(e,t){$.get(Spree.routes.user_search,{ids:e.val()},function(e){t(e.users)})},ajax:{url:Spree.routes.user_search,datatype:"json",data:function(e){return{q:e,token:Spree.api_key}},results:function(e){return{results:e.users}}},formatResult:e,formatSelection:e})},$(document).ready(function(){$(".user_picker").userAutocomplete()});