$(function	()	{
    //scrollable sidebar
    $('.scrollable-sidebar').slimScroll({
        height: '100%',
        size: '0px'
    });

    //Collapsible Sidebar Menu
    $('.sidebar-menu .openable > a').click(function()	{

        if(!$('aside').hasClass('sidebar-mini') || Modernizr.mq('(max-width: 991px)'))	{
            if( $(this).parent().children('.submenu').is(':hidden') ) {
                $(this).parent().siblings().removeClass('open').children('.submenu').slideUp(200);
                //$(this).parent().addClass('open').children('.submenu').slideDown(200);
            }
            /*else	{
             $(this).parent().removeClass('open').children('.submenu').slideUp(200);
             }*/
        }

        return false;
    });

    //Open active menu
    if(!$('.sidebar-menu').hasClass('sidebar-mini') || Modernizr.mq('(max-width: 767px)'))	{
        $('.openable.open').children('.submenu').slideDown(200);
    }

    //Toggle User container on sidebar menu
    $('#btn-collapse').click(function()	{
        $('.sidebar-header').toggleClass('active');
    });

    //theme setting
    $("#theme-setting-icon").click(function()	{
        if($('#theme-setting').hasClass('open'))	{
            $('#theme-setting').removeClass('open');
            $('#theme-setting-icon').removeClass('open');
        }
        else	{
            $('#theme-setting').addClass('open');
            $('#theme-setting-icon').addClass('open');
        }

        return false;
    });

    $('#sidebarToggleLG').click(function()	{
        if($('.wrapper').hasClass('display-right'))	{
            $('.wrapper').removeClass('display-right');
            $('.sidebar-right').removeClass('active');
        }
        else	{
            //$('.nav-header').toggleClass('hide');
            $('.top-nav').toggleClass('sidebar-mini');
            $('aside').toggleClass('sidebar-mini');
            $('footer').toggleClass('sidebar-mini');
            $('.main-container').toggleClass('sidebar-mini');

            $('.main-menu').find('.openable').removeClass('open');
            $('.main-menu').find('.submenu').removeAttr('style');
        }
    });


    $(".church-wrapper .church-body .message-table table tbody tr td").hover(function () {
        $(this).siblings("td").addClass("td-hover-color");
        $(this).addClass("td-hover-color");
    }, function () {
        $(this).siblings("td").removeClass("td-hover-color");
        $(this).removeClass("td-hover-color");
    });



    $(".church-wrapper .church-body .message-table table tbody tr").on('click',function () {
        $('.flex-horizontal.padding-bottom').prepend('<img src="http://static.panoramio.com/photos/original/18257444.jpg" class="foto">');
    });




    $('#transactions').click(function () {
        if ($('li').hasClass('active')) {
            $('li').removeClass('active');

            if ($('li').hasClass('open')) {
                $('li').removeClass('open').children('.submenu').slideUp(200);

            }
        }
        if ($('header').hasClass('sidebar-mini')) {

            $('#transactions').parent("li").addClass('active');
        }
        else {
            $('#transactions').parent("li").addClass('active');
            $('.top-nav').toggleClass('sidebar-mini');
            $('aside').toggleClass('sidebar-mini');
            $('footer').toggleClass('sidebar-mini');
            $('.main-container').toggleClass('sidebar-mini');

            $('.main-menu').find('.openable').removeClass('open');
            $('.main-menu').find('.submenu').removeAttr('style');
        }
    });

    $('#invoices').click(function () {
        if ($('li').hasClass('active')) {
            $('li').removeClass('active');
            if ($('li').hasClass('open')) {
                $('li').removeClass('open').children('.submenu').slideUp(200);

            }
        }
        $('#invoices').parent("li").addClass('active');
        if ($('header').hasClass('sidebar-mini')) {
            $('.top-nav').toggleClass('sidebar-mini');
            $('aside').toggleClass('sidebar-mini');
            $('footer').toggleClass('sidebar-mini');
            $('.main-container').toggleClass('sidebar-mini');

            $('.main-menu').find('.openable').removeClass('open');
            $('.main-menu').find('.submenu').removeAttr('style');
        }
    });


    $('#orders').click(function () {
        if ($('li').hasClass('active')) {
            $('li').removeClass('active');
            if ($('li').hasClass('open')) {
                $('li').removeClass('open').children('.submenu').slideUp(200);

            }
        }
        $('#orders').parent("li").addClass('active');

        if ($('header').hasClass('sidebar-mini')) {
            $('.top-nav').toggleClass('sidebar-mini');
            $('aside').toggleClass('sidebar-mini');
            $('footer').toggleClass('sidebar-mini');
            $('.main-container').toggleClass('sidebar-mini');

            $('.main-menu').find('.openable').removeClass('open');
            $('.main-menu').find('.submenu').removeAttr('style');
        }
    });

    $('#link').click(function () {



        if ($('header').hasClass('sidebar-mini')) {
            $('.top-nav').toggleClass('sidebar-mini');
            $('aside').toggleClass('sidebar-mini');
            $('footer').toggleClass('sidebar-mini');
            $('.main-container').toggleClass('sidebar-mini');

            /*  $('.main-menu').find('.openable').removeClass('open');
             $('.main-menu').find('.submenu').removeAttr('style');*/
        }

        if (!$('#link').parent("li").hasClass('open')) {
            $('#link').parent("li").children('.submenu').slideDown(200);
            $('#link').parent("li").addClass('open');
        } else {
            $('#link').parent("li").children('.submenu').slideUp(200);
            $('#link').parent("li").removeClass('open');
        }
        if ($('li').hasClass('active')) {
            $('li').removeClass('active');

        }
        $('#link').parent("li").addClass('active');
    });


    $('#sidebarToggleSM').click(function()	{
        $('aside').toggleClass('active');
        $('.wrapper').toggleClass('display-left');
    });


    $('.sidebarRight-toggle').click(function()	{
        $('.sidebar-right').toggleClass('active');
        $('.wrapper').toggleClass('display-right');
        $('footer').toggleClass('display-right');

        return false;
    });

    $('.dropdown-menu input').click(function(e) {
        e.stopPropagation(); //This will prevent the event from bubbling up and close the dropdown when you type/click on text boxes.
    });

    //to do list
    $('.task-finish').click(function()	{
        if($(this).is(':checked'))	{
            $(this).parent().parent().addClass('selected');
        }
        else	{
            $(this).parent().parent().removeClass('selected');
        }
    });

    //Delete to do list
    $('.task-del').click(function()	{
        var activeList = $(this).parent().parent();

        activeList.addClass('removed');

        setTimeout(function() {
            activeList.remove();
        }, 1000);

        return false;
    });

    var $activeWidget = '';
    var $activeWidgetHeader;
    var $headerHeight;
    var $screenHeight;
    var $widgetHeight;
    var $borderHeight = 3;

    //Smart Widget

    // Refresh Widget
    $('.widget-refresh-option').click(function()	{

        $activeWidget = $(this).parent().parent().parent();

        var $activeSpinIcon = $activeWidget.find('.refresh-icon-animated').fadeIn();

        setTimeout(function() {
            $activeSpinIcon.fadeOut();
        },2000);

        return false;
    });

    // Collasible Widget
    $('.widget-collapse-option').click(function()	{
        $activeWidget = $(this).parent().parent().parent();

        $activeWidget.find('.smart-widget-inner').slideToggle();
        $activeWidget.toggleClass('smart-widget-collapsed');

        var $activeSpinIcon = $activeWidget.find('.refresh-icon-animated').fadeIn();

        setTimeout(function() {
            $activeSpinIcon.fadeOut();
        },500);

        $activeWidget = '';

        return false;
    });

    //Changing Widget Color
    $('.widget-toggle-hidden-option').click(function()	{
        $activeWidget = $(this).parent().parent().parent();

        $activeWidget.find('.smart-widget-hidden-section').slideToggle();

        var $activeSpinIcon = $activeWidget.find('.refresh-icon-animated').fadeIn();

        setTimeout(function() {
            $activeSpinIcon.fadeOut();
        },500);


        $activeWidget = '';

        return false;
    });

    //Changing Widget Header Background
    $('.widget-color-list li').click(function()	{
        $activeWidget = $(this).parent().parent().parent().parent();
        $selectedColor = $(this).data('color');

        $activeWidget.removeClass('widget-light-grey');
        $activeWidget.removeClass('widget-dark');
        $activeWidget.removeClass('widget-dark-blue');
        $activeWidget.removeClass('widget-blue');
        $activeWidget.removeClass('widget-green');
        $activeWidget.removeClass('widget-yellow');
        $activeWidget.removeClass('widget-orange');
        $activeWidget.removeClass('widget-red');
        $activeWidget.removeClass('widget-purple');

        if($selectedColor != 'reset')
            $activeWidget.addClass($selectedColor);

        return false;
    });

    // Remove Widget
    $('.widget-remove-option').click(function()	{

        $activeWidget = $(this).parent().parent().parent();

        $('#deleteWidgetConfirm').popup('show');

        return false;

    });

    $('.remove-widget-btn').click(function()	{
        $('#deleteWidgetConfirm').popup('hide');
        $activeWidget.fadeOut();

        $activeWidget = '';

        return false;
    });

    //Scroll to Top
    $(".scroll-to-top").click(function()	{
        $("html, body").animate({ scrollTop: 0 }, 600);
        return false;
    });

    // Popover
    $("[data-toggle=popover]").popover();

    // Tooltip
    $("[data-toggle=tooltip]").tooltip();
    $("[rel=tooltip]").tooltip();
});

$(function	()	{

  	//Flot Chart (Total Sales)
	var d1 = [];
	for (var i = 0; i <= 10; i += 1) {
		//d1.push([i, parseInt(Math.random() * 30)]);
		d1 = [[0,700],[1,1200],[2,1100],[3,900],[4,500],[5,700],[6,500],[7,600],[8,1200],[9,1700],[10,1200]];
	}

	function plotWithOptions() {
		$.plot("#placeholder", [d1], {
			series: {
				lines: {
					show: true,
					fill: true,
					fillColor: '#eee',
					steps: false,
					
				},
				points: { 
					show: true, 
					fill: false 
				}
			},

			grid: {
				color: '#fff',
				hoverable: true,
    			autoHighlight: true,
			},
			colors: [ '#bbb'],
		});
	}

	$("<div id='tooltip'></div>").css({
		position: "absolute",
		display: "none",
		border: "1px solid #222",
		padding: "4px",
		color: "#fff",
		"border-radius": "4px",
		"background-color": "rgb(0,0,0)",
		opacity: 0.90
	}).appendTo("body");

	$("#placeholder").bind("plothover", function (event, pos, item) {

		var str = "(" + pos.x.toFixed(2) + ", " + pos.y.toFixed(2) + ")";
		$("#hoverdata").text(str);
	
		if (item) {
			var x = item.datapoint[0],
				y = item.datapoint[1];
			
				$("#tooltip").html("Total Sales : " + y)
				.css({top: item.pageY+5, left: item.pageX+5})
				.fadeIn(200);
		} else {
			$("#tooltip").hide();
		}
	});

	plotWithOptions();

	//Morris Chart (Total Visits)
	var totalVisitChart = Morris.Bar({
	  element: 'totalSalesChart',
	  data: [
	    { y: '2008', a: 100, b: 90 },
	    { y: '2009', a: 75,  b: 65 },
	    { y: '2010', a: 50,  b: 40 },
	    { y: '2011', a: 75,  b: 65 },
	    { y: '2012', a: 50,  b: 40 },
	    { y: '2013', a: 75,  b: 65 },
	    { y: '2014', a: 100, b: 90 }
	  ],
	  xkey: 'y',
	  ykeys: ['a', 'b'],
	  labels: ['Total Visits', 'Bounce Rate'],
	  barColors: ['#999', '#eee'],
	  grid: false,
	  gridTextColor: '#777',
	});
	

	//Datepicker
	$('#calendar').DatePicker({
		flat: true,
		date: '2014-06-07',
		current: '2014-06-07',
		calendars: 1,
		starts: 1
	});

	//Skycon
	var icons = new Skycons({"color": "white"});
    icons.set("skycon1", "sleet");
    icons.set("skycon2", "partly-cloudy-day");
    icons.set("skycon3", "wind");
    icons.set("skycon4", "clear-day");
    icons.play();

	//Scrollable Chat Widget
	$('#chatScroll').slimScroll({
		height:'230px'
	});

	//Chat notification
	setTimeout(function() {
		$('.chat-notification').find('.badge').addClass('active');
		$('.chat-alert').addClass('active');
	}, 3000);

	setTimeout(function() {
		$('.chat-alert').removeClass('active');
	}, 8000);
	
	$(window).resize(function(e)	{
		// Redraw All Chart
		setTimeout(function() {
			totalVisitChart.redraw();
			plotWithOptions();
		},500);
	});

	$('#sidebarToggleLG').click(function()	{
		// Redraw All Chart
		setTimeout(function() {
			totalVisitChart.redraw();
			plotWithOptions();
		},500);
	});

	$('#sidebarToggleSM').click(function()	{
		// Redraw All Chart
		setTimeout(function() {
			totalVisitChart.redraw();
			plotWithOptions();
		},500);
	});
});
